mapper.update:
update t_person
department = #{departmentId}, job = #{jobId}, edu_level = #{eduLevelId},
passwd = #{passwd}, authority = #{authority}, name = #{name}, sex = #{sex}, birthday = #{birthday} specialty = #{specialty}, address = #{address}, tel = #{tel}, email = #{email}, state = #{state}, remake = #{remake}
where id = #{id};

mapper.select:
select
  p.id,p.name,p.passwd,p.sex,d.name department_name,e.description edu_level_name,j.description job_name ,p.authority,p.address,p.birthday,p.email,p.remake,p.specialty,p.tel,p.state
from
  t_person p, t_department d, t_job j, t_edu_level e

    p.department = d.id and p.job = j.code and p.edu_level = e.code
    and p.id = {id}
    and p.name = #{name}
    and sex = #{sex}
    and d.name = #{departmentName}
    and j.description = #{jobName}
    and remake = #{remake}

    and passwd = #{passwd}
    and authority = #{authority}
    and birthday = #{birthday}
    and e.description = #{eduLevelName}
    and specialty = #{specialty}
    and address = #{address}
    and tel = #{tel}
    and email = #{email}
    and state = #{state}

select count(*) from
t_person p, t_department d, t_job j, t_edu_level e

    p.department = d.id and p.job = j.code and p.edu_level = e.code
    and p.id = {id}
    and p.name = #{name}
    and sex = #{sex}
    and d.name = #{departmentName}
    and p.job = #{jobId}
    and j.description = #{jobName}
    and remake = #{remake}

    and passwd = #{passwd}
    and authority = #{authority}
    and birthday = #{birthday}
    and e.description = #{eduLevelName}
    and specialty = #{specialty}
    and address = #{address}
    and tel = #{tel}
    and email = #{email}
    and state = #{state}
